import React,{ useState} from 'react';
import { routes } from './utils/routes';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Navbar = () => {

  const [buttonColor, setButtonColor] = useState('initialColor');

  const changeColor = () => {
    if (buttonColor === 'initialColor') {
      setButtonColor('light');
    } else {
      setButtonColor('dark');
    }
  }
  return (
    <div className="navbar navbar-expand-lg bg-body-tertiary">
      <Link to={routes.home} className=''  >Home</Link>
      <Link to={routes.contact}  >Contact</Link>
      <Link to={routes.about}  >Favs</Link>
      <Link to={routes.detail}  >Detail</Link>
      <button onClick={changeColor} style={{ backgroundColor: buttonColor }}>Cambiar de color</button>
    </div>
  )
}

export default Navbar
